function foo(array) {
  let sum = 0;
  let product = 1;
  for (let i = 0; i < array.length; i++) {
    sum += array[i];
  }
  for (i = 0; i < array.length; i++) {
    product *= array[i];
  }
  console.log(sum + ", " + product);
}

